document.addEventListener("DOMContentLoaded",()=>{const n=document.getElementById("bmi-form"),t=document.getElementById("result-box"),e=document.getElementById("bmi-value"),o=document.getElementById("bmi-category"),d=document.getElementById("bmi-feedback");n.addEventListener("submit",n=>{n.preventDefault();const c=parseFloat(document.getElementById("height").value),m=parseFloat(document.getElementById("weight").value);if(!c||!m)return;const h=c/100,i=m/(h*h),g=i.toFixed(1);e.textContent=g;let u="",a="",l="";i<18.5?(u="Nhẹ cân (Gầy)",a="text-warning",l="Bạn cần bổ sung dinh dưỡng để tăng cân."):i<23?(u="Bình thường",a="text-success",l="Tuyệt vời! Hãy duy trì chế độ ăn uống và luyện tập hiện tại."):i<25?(u="Thừa cân (Tiền béo phì)",a="text-warning",l="Bạn nên chú ý ăn uống lành mạnh và vận động nhiều hơn."):i<30?(u="Béo phì độ I",a="text-danger",l="Cảnh báo! Bạn nên giảm cân để tránh nguy cơ bệnh lý."):(u="Béo phì độ II",a="text-danger",l="Nguy hiểm! Hãy tham khảo ý kiến bác sĩ để giảm cân an toàn."),o.textContent=u,o.className=a,d.textContent=l,t.style.display="block",t.scrollIntoView({behavior:"smooth"})})});