function formatJSON(){const e=document.getElementById("json-input"),t=document.getElementById("json-error");try{const n=e.value;if(!n)return;const o=JSON.parse(n);e.value=JSON.stringify(o,null,2),t.style.display="none"}catch(e){t.style.display="block",t.textContent="❌ Invalid JSON: "+e.message}}function minifyJSON(){const e=document.getElementById("json-input"),t=document.getElementById("json-error");try{const n=e.value;if(!n)return;const o=JSON.parse(n);e.value=JSON.stringify(o),t.style.display="none"}catch(e){t.style.display="block",t.textContent="❌ Invalid JSON: "+e.message}}function copyJSON(){document.getElementById("json-input").select(),document.execCommand("copy"),alert("Copied to clipboard!")}function clearEditor(){document.getElementById("json-input").value="",document.getElementById("json-error").style.display="none"}